import React from 'react';
import './styles/App.css';
import { Switch, Route, Link } from 'react-router-dom';

//components
import Login from './components/Login'
import Register from './components/Register';
import Homepage from './components/Homepage';
import Dashboard from './components/Dashboard';
import About from './components/About';

//images
import logo from './images/logo.png'

function App() {

  const colorPalette = {
    darkgreen: '#026565',
    lightgreen: '#C5DEDE',
    white: '#FFFFFF',
    offwhite: '#F0F6F6',
    peach: '#FC8372',
    black: '#232424'
  }

  const linkStyles = {
    textDecoration: 'none',
    margin: '0 auto',
    color: colorPalette.lightgreen,
    paddingLeft: '40px'
  }
  return (
    <div className="App">

      <nav style={{backgroundColor: colorPalette.darkgreen}}>
        <div style={{paddingLeft: '30px'}}>
          <Link to='/'><img src={logo} alt='logo' style={{width: '200px'}}/></Link>
        </div>
        <div style={{padding: '10px 30px 0 0'}}>
          <Link to='/' style={linkStyles}>Home</Link>
          <Link to='/dashboard' style={linkStyles}>Dashboard</Link>
          <Link to='/about' style={linkStyles}>About</Link>
          <Link to='/register' style={linkStyles}><button className="register-btn">Register</button></Link>
        </div>
      </nav>

      <Switch>
        {/* protected route */}
        <Route exact path='/' component={Homepage}/>
        <Route path='/login' component={Login}/>
        <Route path='/register' component={Register}/>
        <Route path='/dashboard' component={Dashboard}/>
        <Route path='/about' component={About}/>
      </Switch>
    </div>
  );
}

export default App;
