import React, { useState, useEffect} from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import * as yup from "yup";
import { TextField } from '@material-ui/core';

const schema = yup.object().shape({
    username: yup.string().required('Username is a required field.'),
    password: yup.string().required('Password is a required field.')
})

const Login = () => {

    const [errorState, setErrorState] = useState({
        username: '',
        password: ''
    })

    const validator = (e) => {
        yup
          .reach(schema, e.target.name)
          .validate(e.target.value)
          .then((valid) => {
            setErrorState({
              ...errorState,
              [e.target.name]: '',
            });
          })
          .catch((err) => {
            setErrorState({
              ...errorState,
              [e.target.name]: err.errors[0],
            });
          });
      };

    const [data, setData] = useState([]);  

    const [userCredentials, setuserCredentials] = useState({
        username: '',
        password: ''
    })

    const changeHandler = e => {
        e.persist();
        validator(e);
        setuserCredentials({ ...userCredentials, [e.target.name]: e.target.value });
        // console.log(e)
    }

    const submitHandler = e => {
        //prevents automatic refresh
        e.preventDefault();
        //resets form after submitted
        setuserCredentials({
            username: '',
            password: ''
        })
        // console.log(e)

        axios
            .post(`https://reqres.in/api/users`, userCredentials)
            .then(res => setData(res))
            .catch(err => {console.log(err.res)})
        console.log('Login successful')    
    }

    return (
        <div className="container">
            <h1>Welcome back!</h1>
            <form onSubmit={submitHandler}>
                <div>
                    <label htmlFor='username'>Username</label>
                    <input
                        type="text"
                        id="username"
                        name="username"
                        value={userCredentials.username}
                        onChange={changeHandler}
                    />
                </div>
                <div>
                    <label htmlFor='password'>Password</label>
                    <input
                        type="password"
                        id="password"
                        name="password"
                        value={userCredentials.password}
                        onChange={changeHandler}
                    />
                </div>
                <div>
                                        <label htmlFor="username"></label><br/>
                                        <TextField
                                            id="standard-basic" 
                                            label="Username"
                                            type="text"
                                            name="username"
                                            fullWidth
                                            value={userInput.username}
                                            onChange={changeHandler}
                                            helperText={errorState.username.length > 0 ? (<p className="error">{errorState.username}</p>) : null}
                                        /><br/>

                                        <label htmlFor="password"></label><br/> 

                                        <TextField
                                            type={passwordInputType}
                                            id="standard-basic" 
                                            label="Password"
                                            name="password"
                                            fullWidth
                                            value={userInput.password}
                                            onChange={changeHandler}
                                            helperText={errorState.password.length > 0 ? (<p className="error">{errorState.password}</p>) : null}
                                        />
                                        <div><span className="password-icon-login">{ToggleIcon}</span></div>
                                </div>
                <button type='submit'>Submit</button>
                <pre>{JSON.stringify(data, null, 2)}</pre>
            </form>
            <p>Don't have an account? <Link to='/register'>Register</Link></p>
        </div>
    )
}

export default Login;
